import React, {Component} from 'react';
import LoginPage from './Components/LoginPage';
import SettingTemplatePage from './Components/SettingTemplatePage';
import ForgotPage from './Components/ForgotPage';
import ConfirmPage from './Components/ConfirmPage';
import CreatePage from './Components/CreatePage';
import MemPage from './Components/MemPage';
import Trip from './Components/Trip';
import Transaction from './Components/Transaction';
import Task from './Components/Task';
import Members from './Components/Members';
import Accounting from './Components/Accounting';
import AddTripPage from './Components/AddTripPage';
import ImportMemberPage from './Components/ImportMemberPage';
import ConfirmImport from './Components/ConfirmImport';
import ShareLinkPage from './Components/ShareLinkPage';

class App extends Component {
  constructor() {
    super()
    this.state = {
      page: "",
      user: {
        id : '',
        avatar: '',
        email: '',
        name: '',
        trips: [],
      },
      trip: {
        id: '',
        avatar: '',
        name: '',
        events: [],
      }
    }
  }

  changePage = (nextPage) => {
    this.setState({
      page: nextPage
    });
  };

  loadUser = (data) => {
    console.log(data)
    this.setState({ user: {
      id: data.id,
      avatar: data.avatar,
      email: data.email,
      name: data.name,
      trips: data.trips,
    }})
  }

  loadTrip = (data) => {
    console.log(data)
    this.setState({ trip : {
      id: data.id,
      avatar: data.avatar,
      name: data.name,
      events: data.events,
    }})
  }

  render() {
    let {page} = this.state;

    switch(page) {
      case "trip": 
      let s = ['profile', 'username', 'privacy'];
        return (
          <div className='tc flex flex-column'>
            <SettingTemplatePage 
              avatar="https://scontent-lax3-1.xx.fbcdn.net/v/t1.0-9/14264830_372615786419976_2056481598518504979_n.jpg?_nc_cat=101&_nc_sid=85a577&_nc_ohc=gyeXJCdSTpgAX_XqKVh&_nc_ht=scontent-lax3-1.xx&oh=644a3bfccdd670740976f8ab8617f03a&oe=5EA4E241"
              settings={s}
              changePage={this.changePage} />
          </div>
        );

      case "create":
        return (
          <CreatePage 
            changePage={this.changePage}
            loadUser={this.loadUser}
          />
        )

      case "import":
        return (
          <ImportMemberPage />
        )

      case "mem":
        return (
          <Members />
        );

      case "sharelink":
        return (
          <ShareLinkPage 
          />
        )

      case "forget":
        return (
          <ForgotPage 
            changePage={this.changePage} 
          />
        )

      case "confirmImport":
        return (
            <ConfirmImport
              changePage={this.changePage}
              groupName='Utah Trip'
              ava="https://upload.wikimedia.org/wikipedia/commons/thumb/8/86/%27David%27_by_Michelangelo_Fir_JBU013.jpg/165px-%27David%27_by_Michelangelo_Fir_JBU013.jpg"
              link="http://pronhup.com"
            />
        )

      case "addtrip":
        return (
          <AddTripPage
            changePage={this.changePage}
            admin={this.state.user.username}
          />
        )

      case "confirm":
        return (
          <ConfirmPage 
            changePage={this.changePage}
            email={this.state.email}
          />
        )

      case "memlink":
        return (
          <MemPage 
            changePage={this.changePage}
            group="utah trip"
          />
        )

      case "trips":
        return (
          <Trip
            user={this.state.user} 
            changePage={this.changePage}
            loadTrip={this.loadTrip}
            type="trip" 
          />
        )

      case "trans":
        return (
          <Transaction />
        )

      case "tasks":
        return (
          <Task
            changePage={this.changePage}
            type="task"
          />
        )

      case "acc":
        return (
          <Accounting 
            changePage={this.changePage}
          />
        )

      default: 
        return (
          <div className='tc flex flex-column'>
            <LoginPage 
              changePage={this.changePage}
              loadUser={this.loadUser}
            />
         </div>
        );
    }
  }
}

export default App;
