import React, {Component} from 'react';
import BackBt from './Common/BackBt';
import InputLine from './Common/InputLine';
import Butt from './Common/Butt';
import './Common/ShareLinkPage.css';

export default class ShareLinkPage extends Component {
	constructor(props) {
		super(props);
		this.state={
			delEmail: '',
			currentEmail: '',
			tripName:this.props.tripName,
			emails:[
				'a@g.com',
				'b@g.com',
				'd@g.com'
			]
		}
	}

	back = () => {
		this.props.changePage('confirmImport');
	}

	share = () => {
		console.log('shared');

		//TODO: set root state trip name here to make query call

		this.props.changePage('trips');
	}

	onFieldChange = (event) => {
		this.setState({currentEmail: event.target.value});
	}

	onSubmit = () => {
		//Create new thingie here
		this.setState({currentEmail: ''})
	}

	add = () => {
		let newEmails = this.state.emails;
		newEmails.push(this.state.currentEmail);
		this.setState({
			currentEmail: '',
			emails: newEmails
		})
	}

	deleteEntry = (event) => {
		console.log(event.target.value)
		let eList = this.state.emails;
		eList.filter(em => {
			return em !== event.target.value;
		});
		this.setState({emails: eList});
	}

	render() {
		return (
			<div>
				<BackBt 
					onClick={this.back}
				/>
				<div className='flex flex-column'>
					<div className='pa2 center tc w-50'>
						<InputLine
							type='email'
							value='Enter email'
							onChange={this.onFieldChange}
						/>
					</div>

					<div className='pa2 tc'>
						<Butt
							value='Add'
							onClick={this.add}
						/>
					</div> 
					<div className='pa2 tc flow vh-50'>
						<MemList
							del={this.props.deleteEntry}
							list={this.state.emails}
						/>
					</div>
					<div className='pa1 tc'>
						<Butt 
							onClick={this.share}
							value='Share'
						/>
					</div>
				</div>
			</div>
		)
	}
}

class MemList extends Component {

	render() {
		const list = this.props.list;

		var rows = list.map((entry, i) => {
			return
				<MemRow 
					key={i}
					del={this.props.del}
					email={entry} 
				/>
		})
		return (
			<div>
				{rows}
			</div>
		)
	}
} 

class MemRow extends Component {

	render() {
		const {email} = this.props;
		return (
			<div className='flex justify-center'>
				<div className='pa1'>
					<p className='tc white f5 underline'> 
						{email}
					</p>
				</div>
				<div className='pl3 pr3 pt3'>
					<button 
						value={email}
						onClick={this.props.del}
						className='white'
						style={{border:"none", backgroundColor: "transparent"}}>
							X
					</button>
				</div>
			</div>
		)
	}
}
